    var 命身宮論斷 = 命身宮論斷單元(命盤);
    var 分定經論斷 = 分定經(命盤.年干.天干, 命盤.時干.天干, 0);
    var 六十日主用法詩訣論斷 = 六十日主用法詩訣(命盤.日柱.干支);
    var 六十日主單元=六十日主論斷單元(命盤);
    
    //var 六十日主論斷1註解=六十日主論斷1(命盤.日柱.干支);
    //var 六十日主論斷2註解=六十日主論斷2(命盤.日柱.干支);

    傳統論斷內容矩陣.push(命盤神煞論斷);
    傳統論斷內容矩陣.push(四柱神煞論斷);
    
    //傳統論斷內容矩陣.push(四柱坐運星論斷);
    傳統論斷內容矩陣.push(命身宮論斷);
