function 經典參考函式(命盤, 選項){
    var 經典資料=[];
    if(選項 == undefined) 選項 = "PanelGroup"; // ，temporary not in used

    var 八字提要註解 = {"經典": "八字提要", "經典註解": 韋千里八字提要(命盤.日干.天干 + 命盤.月支.地支 + 命盤.時柱.干支)};
    經典資料.push(八字提要註解);

    var 滴天髓十干註解 = {"經典": "滴天髓", "經典註解": 滴天髓十干(命盤.日主)};
    經典資料.push(滴天髓十干註解);

    var 淵海子平十干註解 ={"經典": "淵海子平", "經典註解": 淵海子平十干(命盤.日主) };
    經典資料.push(淵海子平十干註解);

    var 窮通寶鑑註解={"經典": "窮通寶鑑", "經典註解": 窮通寶鑑(命盤.日主, 命盤.月令) };
    經典資料.push(窮通寶鑑註解);

    var 造化元鑰註解={"經典": "造化元鑰", "經典註解": 造化元鑰(命盤.月令+"月"+命盤.日干.天干) };
    經典資料.push(造化元鑰註解);

    var 三命通會十干論斷 = 三命通會十干(命盤.日主);
    var 三命通會日柱生時論斷 = 三命通會日柱生時(命盤.日柱.干支 + 命盤.時柱.干支);
    var 三命通會={"經典": "三命通會", "經典註解": 三命通會十干論斷 + "<br><br>" + 三命通會日柱生時論斷 };
    經典資料.push(三命通會);

    //var 十干喜忌訣1註解={"經典": "十干喜忌訣", "經典註解": 十干喜忌訣1(命盤.日主) };
    //經典資料.push(十干喜忌訣1註解);

    switch (選項){
        case "PanelGroup":
            return ObjArry2PanelGrp(經典資料, "經典參考", "經典");
        case "窮通寶鑑":
            return 窮通寶鑑註解;
        case "淵海子平十干":
            return 淵海子平十干註解;
        case "滴天髓十干":
            return 滴天髓十干註解;
    } // end switch

    //$("#nrxs-經典參考").html(ObjArry2PanelGrp(經典資料, "經典參考", "經典"));
} // end 



