function 十干喜忌訣1(搜索的天干, 標題, 選項, TagChg) {
    // to delete, 5/Oct/2022
	// 標題：放在搜文的前端，比方 《滴天髓：》
    // 選項：Return 那一段資料
    // TagChg：<br>tag 的文字轉換，比方將<br> 換成 carriage return
    // 選項：原文，原注（原文+原注），補注（原文+原注+補注），全文

    if(標題 == undefined) 標題 = "";
    if(選項 == undefined) 選項 = "全文";

    var 十干喜忌訣1資料 = [
        { "天干": "甲", "原文": "最喜庚金伐，斫削成大才；辛為柔弱金，木堅金易折。見戊利經商，財大腰氣粗；己來合甲木，命主手腳縛。壬為汪洋水，無寅隨波逐；癸為雲雨水，夏解炎燥苦。丙為太陽火，冬春可暖局；丁使甲外柔，無庚人虛偽。乙為透天刃，合庚失貴氣。"},
        { "天干": "乙", "原文": "丙能去陰柔，木火通明格；丁使乙萎蔫，見辛成武貴。戊為剛硬土，柔木無力伐；己土名田園，反哺乙木發。庚來性多疑，難有用武地；辛能絆丙火，難以發秀氣。壬掀浪滔滔，弱木懼漂泊；癸水潤花木，不懼燥火烈。見甲系藤蘿，四時皆相宜。"},
        { "天干": "丙", "原文": "逢甲愈剛烈，無庚多招禍；乙木具理智，木火通明格。丁為丙之刃，少金殺氣重；戊來性過剛，取貴壬調節。己可去火燥，卻失光明節；庚生壬抑丙，財富滿倉筐。辛來投懷抱，忘義戀情色；壬可驅炎燥，顯威居權要。烏雲癸遮日，光芒難普照。"},
        { "天干": "丁", "原文": "甲現須庚劈，事業可大成；乙生火微弱，丁難鍛頑鐵。丙可去憂郁，命主顯生機；戊遇甲或金，以智成巨富。己為命主壽，心寬好口福；庚金須帶甲，無甲命貧苦。辛透人慷慨，財來似潮涌；壬現合官貴，有甲富貴臨。癸須甲或戊，七殺化權柄。"},
        { "天干": "戊", "原文": "甲疏土有用，無甲枉費心；乙官性柔弱，難以成大貴。丙火照頑土，寸草不易生；丁火功在鐵，星火難照明。己逢甲或丙，稼穡成富翁；有庚才華溢，立業建大功。辛須丙癸現，出入顯門中；見壬成堤岸，雖苦事業成。癸財長流水，源源庫能存；巳月祿當權，厭甲喜癸潤。"},
        { "天干": "己", "原文": "甲來貴合身，無甲性吊滯；乙乏克伐力，碌碌一無成。丙火照陰土，萬物得滋生；丁為燈燭光，難得顯大功。戊須甲來疏，無甲性頑冥；庚金可泄秀，豁達重信誠。見辛貪財色，處處留情種；見壬則智濁，邪道起歪風。癸水配丙甲，事業方發達。"},
        { "天干": "庚", "原文": "甲財利潤厚，劈之可發榮；乙卻束手腳，吝嗇守財奴。丙為太陽光，雖烈不煉金，丁火鍛成器，顯貴入公門。戊土性干爽，泄秀不污壬；己土陰而濁，貌丑性下品。見辛須甲丁，無則性毒狠；逢壬金水清，才貌最可人。癸來傷丁貴，叛逆成霸匪。"},
        { "天干": "辛", "原文": "甲為剛硬木，日主遭反侮；乙木性柔弱，適制財豐厚。有丙戀財色，壯志被削磨；丁毀珠玉金，易成傷殘身。戊硬不生身，才華受拘禁；己土污水濁，辛金失光澤。壬水金清白，冬生喜丙火；癸為雲雨露，難列富貴格。庚來扶弱生，不懼泄耗克。"},
        { "天干": "壬", "原文": "甲現好灌溉，慧質成大才；乙畏天河倒，隨波不自在。丙火水既濟，財富排天來；見丁酒色客，財從手中過。戊土堪作堤，威武鎮邪惡；己混不清沏，一生昏噩噩。庚為水之源，不懼土木火，辛白水愈清，才華蓋世歌。癸來劫財去，又將妻夫奪。"},
        { "天干": "癸", "原文": "甲木參天樹，雨露無奈何；乙木為花草，滴水不干渴。丙火映彩雲，絢麗壯山河；丁火光微弱，有財能幾何？戊來合俊秀，老翁或丑婆；見己水不清，智商打對折。庚生怕太過，金多水必濁；辛癸兩相見，金白水清格。壬兄助身旺，不怕火土多。"}
    ];

    var 資料索引=_.findIndex(十干喜忌訣1資料, {天干: 搜索的天干});
    var 索文="", returnStr="";
    if (資料索引 != -1) {
        switch (選項) {
            case "原文":
                索文=十干喜忌訣1資料[資料索引].原文;
                break;
            default:
                索文=十干喜忌訣1資料[資料索引].原文;
        } //end switch
        if(TagChg != undefined) returnStr = 索文.replace("<br>", TagChg); else returnStr = 索文;
    } // end if
    
    return returnStr;
}